#include <bits/stdc++.h>
using namespace std;
using lint = long long;
using pi = array<lint, 2>;
#define sz(v) ((int)(v).size())
#define all(v) (v).begin(), (v).end()

int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	int t;
	cin >> t;
	while (t--) {
		int n;
		cin >> n;
		map<int, int> cnt;
		int g = 0;
		for (int i = 2; i * i <= n; i++) {
			while (n % i == 0) {
				cnt[i]++;
				g = max(g, cnt[i]);
				n /= i;
			}
		}
		if (n > 1)
			cnt[n]++, g = max(g, 1);
		vector<int> ans(g, 1);
		for (auto &[g, n] : cnt) {
			for (int z = 1; z <= n; z++) {
				ans[z - 1] *= g;
			}
		}
		cout << accumulate(all(ans), 0ll) << "\n";
	}
}
