#include<bits/stdc++.h>
#define L(i, j, k) for(int i = (j); i <= (k); ++i)
#define R(i, j, k) for(int i = (j); i >= (k); --i)
#define ll long long 
#define vi vector < int > 
#define sz(a) ((int) (a).size())
#define ll long long 
#define ull unsigned long long
#define me(a, x) memset(a, x, sizeof(a)) 
using namespace std;
const int N = 1e6 + 7;
int a[N];
int n, k;
vi vc[N];

int ans[N];
void Main() {
	cin >> n >> k;
	L(i, 1, n) {
		cin >> a[i];
		vc[a[i]].emplace_back(i);
	}
	int l = n, r = 1; 
	R(i, k, 1) {
		if(!sz(vc[i])) {
			ans[i] = 0;
			continue;
		}
		for(auto &u : vc[i]) {
			l = min(l, u);
			r = max(r, u);
		}
		ans[i] = (r - l + 1) * 2;
	}
	L(i, 1, k) {
		cout << ans[i] << ' ';
	}
	cout << '\n';
	L(i, 1, k) {
		vc[i].clear();
	}
}
int main() {
	ios :: sync_with_stdio(false);
	cin.tie(0); cout.tie(0);
	int t; cin >> t; while(t--) Main();
	return 0;
}
