#include <bits/stdc++.h>
using namespace std;
using lint = long long;
using pi = array<lint, 2>;
#define sz(v) ((int)(v).size())
#define all(v) (v).begin(), (v).end()

int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	int t;
	cin >> t;
	while (t--) {
		int n, s;
		cin >> n >> s;
		vector<pi> a(n);
		for (int i = 0; i < n; i++) {
			int x;
			cin >> x;
			if (x >= 2 * s)
				a[i] = {x - s, s};
			else
				a[i] = {x - min(x, s), min(x, s)};
		}
		vector<pi> dp(n + 1);
		dp[1][0] = (a[0][0] + a[0][1]) * a[1][0];
		dp[1][1] = (a[0][0] + a[0][1]) * a[1][1];
		for (int i = 2; i < n; i++) {
			for (int j = 0; j < 2; j++) {
				dp[i][j] = min(dp[i - 1][0] + a[i - 1][1] * a[i][j], dp[i - 1][1] + a[i - 1][0] * a[i][j]);
			}
		}
		cout << min(dp[n - 2][0] + a[n - 2][1] * (a[n - 1][0] + a[n - 1][1]), dp[n - 2][1] + a[n - 2][0] * (a[n - 1][0] + a[n - 1][1])) << "\n";
	}
}
