#include <iostream>
using namespace std;
const int MAX = 100005;
int a[MAX];
int main()
{
	ios::sync_with_stdio(false);
	int n, k;
	cin >> n >> k;
	int ans = 2 * n - k;
	for (int i = 0; i < k; i++)
	{
		int x;
		cin >> x;
		for (int j = 0; j < x; j++)
			cin >> a[j];
		if (a[0] == 1)
		{
			int pos = 1;
			while (pos < x && a[pos] == pos + 1)
				pos++;
			ans -= 2 * pos - 1;
		}
	}
	cout << ans << endl;
	return 0;
}
