#include <iostream>
using namespace std;
int p[10];
void go(int x, int sign)
{
	for (int i = 2; i < 10; i++)
		while (x % i == 0)
		{
			x /= i;
			p[i] += sign;
		}
}
int main()
{
	ios::sync_with_stdio(false);
	int n;
	cin >> n;
	for (int i = 0; i < n; i++)
	{
		char c;
		cin >> c;
		int val = 1;
		int f = c - '0';
		for (int j = 1; j <= f; j++)
			val *= j;
		go(val, 1);
	}
	string ans = "";
	for (int i = 9; i >= 2; i--)
		while (p[i] > 0)
		{
			ans += char(i + '0');
			int val = 1;
			for (int j = 1; j <= i; j++)
				val *= j;
			go(val, -1);
		}
	cout << ans << endl;
	return 0;
}
