#include <cstdio>

#define MOD 1000000007

int main() {
//  freopen("in.txt", "r", stdin);
//  freopen("out.txt", "w", stdout);

    long long a, b, rlt;

    scanf("%I64d%I64d", &a, &b);
    rlt = (b * (b - 1) / 2) % MOD;
    rlt = rlt * a % MOD * ((a * b + b + 2) % MOD) % MOD;
    if (rlt & 1) rlt += MOD;
    rlt /= 2;
    printf("%I64d\n", rlt % MOD);
    return 0;
}
