int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int tt;
  cin >> tt;
  while (tt--) {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) {
      cin >> a[i];
    }
    vector<int> cnt(n + 3);
    for (int i = 0; i < n; i++) {
      cnt[a[i]] += 1;
    }
    bool met = false;
    for (int i = 0; i < int(cnt.size()); i++) {
      if (cnt[i] == 0 || (cnt[i] == 1 && met)) {
        cout << i << '\n';
        break;
      }
      met |= (cnt[i] == 1);
    }
  }
  return 0;
}
