#include <bits/stdc++.h>
using namespace std;


using ll = int64_t;
void solve(){
	int N;
	cin >> N;
	vector<ll> A(N);
	for(ll& x : A) cin >> x;
	sort(A.begin(), A.end());
	ll b = 1;
	ll ans = 0;
	for(ll& x : A){
		if(x > b){
			ans += x-b;
			x = min(x, b);
		}
		if(x == b) b++;
	}
	cout << ans << '\n';
}

int main(){
	ios_base::sync_with_stdio(false), cin.tie(nullptr);
	int T;
	cin >> T;
	while(T--) solve();
}
