int main() {
    ios_base::sync_with_stdio(false);
    LL x, y, m;
    cin >> x >> y >> m;
    if (max(x, y) >= m) {
        cout << 0 << endl;
        return 0;
    }
    if (x > y) {
        swap(x, y);
    }
    if (y <= 0) {
        cout << -1 << endl;
        return 0;
    }
    LL result = 0;
    if (x < 0) {
        result = (-x) / y;
        x += result * y;
    }
    while (max(x, y) < m) {
        x += y;
        ++result;
        if (x > y) {
            swap(x, y);
        }
    }
    cout << result << endl;
}
