using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define snuke(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

set <int> bad;
int a[100010];

int main(void){
    int N,K,i;

    cin >> N >> K;
    REP(i,N) scanf("%d", &a[i]);
    sort(a, a+N);

    int ans = 0;
    for(i=N-1;i>=0;i--){
        if(bad.find(a[i]) == bad.end()){
            ans++;
            if(a[i] % K == 0) bad.insert(a[i] / K);
        }
    }

    cout << ans << endl;

    return 0;
}
