clock_t startTime;
double getCurrentTime() {
	return (double)(clock() - startTime) / CLOCKS_PER_SEC;
}

int p10[8];

void solve() {
	int A, B, C;
	ll k;
	scanf("%d%d%d%lld", &A, &B, &C, &k);
	for (int x = p10[A - 1]; x < p10[A]; x++) {
		int l = max(p10[B - 1], p10[C - 1] - x);
		int r = min(p10[B], p10[C] - x);
		if (l >= r) continue;
		if (k > (r - l)) {
			k -= r - l;
			continue;
		}
		int y = l + k - 1;
		printf("%d + %d = %d\n", x, y, x + y);
		return;
	}
	printf("-1\n");
}

int main()
