#include <ctime>
#include <cstdio>

using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define foreach(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

vector <pair <int, int> > cup[60]; // id, amount

int main(void){
    int N,M,W,i,j;

    cin >> N >> W >> M;
    if(M > N && N % (M - N) != 0){
        cout << "NO" << endl;
        return 0;
    }

    REP(i,N){
        int low = i*M, high = (i+1)*M, mid = low/N*N + N;
        if(high <= mid){
            cup[low/N].push_back(make_pair(i+1,M));
        } else {
            cup[low/N].push_back(make_pair(i+1,mid-low));
            cup[low/N+1].push_back(make_pair(i+1,high-mid));
        }
    }

    cout << "YES" << endl;
    REP(i,M) REP(j,cup[i].size()){
        int id = cup[i][j].first;
        double amount = (double)cup[i][j].second * W / M;
        printf("%d %.6f",id,amount);
        if(j == cup[i].size() - 1) printf("\n"); else printf(" ");
    }

    return 0;
}
