#include <cstdio>
#include <cstring>
#include <algorithm>
#define FOR(i,s,e) for (int i=(s); i<(e); i++)
#define FOE(i,s,e) for (int i=(s); i<=(e); i++)
#define FOD(i,s,e) for (int i=(s)-1; i>=(e); i--)
#define CLR(a,x) memset(a, x, sizeof(a))
#define EXP(i,l) for (int i=(l); i; i=qn[i])
#define N 100005
using namespace std;

int n, m, cnt;
int v[N], nod[N], a[N], L[N];
char op[N][5];

int main(){
	scanf("%d%d", &n, &m);
	CLR(v, -1);
	FOR(i,0,m){
		scanf("%s%d", &op[i], &a[i]), --a[i];
		if (v[a[i]] == -1) v[a[i]] = (op[i][0] == '+' ? 1 : 0);
	}

	int sz = 0;
	FOR(i,0,n){
		if (v[i] == 0) ++cnt;
		if (v[i] == 1) L[sz++] = i;
	}

	if (cnt > 0){
		while (sz) nod[L[--sz]] = 1;
	}

	FOR(i,0,m){
		if (op[i][0] == '+'){
			if (cnt > 0) nod[a[i]] = 1;
			++cnt;
			while (sz){
				--sz;
				if (L[sz] != a[i]) nod[L[sz]] = 1;
			}
		}
		else{
			--cnt;
			if (cnt > 0) nod[a[i]] = 1;
			if (!nod[a[i]]) L[sz++] = a[i];
		}
	}

	int ret = n;
	FOR(i,0,n) ret -= nod[i];
	printf("%d\n", ret);
	FOR(i,0,n) if (!nod[i]) printf("%d ", i + 1);
	return 0;
}
