using ll=long long;
using pii=pair<int,int>;
using pll=pair<ll,ll>;
using vi=vector<int>;
using vll=vector<ll>;
const int nax=1000*1007;
const ll mod=998244353;

int n;
int tab[nax];

vi graf[nax];
int sto[nax];

int blok[nax];
int biore[nax];
int out[nax];

void nope()
{
	printf("-1\n");
	exit(0);
}

int main()
{
	scanf("%d", &n);
	for (int i=1; i<=n; i++)
		scanf("%d", &tab[i]);
	for (int i=1; i<=n; i++)
	{
		graf[tab[i]].push_back(i);
		sto[tab[i]]++;
	}
	queue<int> kol;
	for (int i=1; i<=n; i++)
		if (!sto[i])
			kol.push(i);
	while(!kol.empty())
	{
		int v=kol.front();
		kol.pop();
		out[v]=1;
		if (!blok[v])
		{
			blok[tab[v]]=1;
			biore[v]=1;
		}
		sto[tab[v]]--;
		if (!sto[tab[v]])
			kol.push(tab[v]);
	}
	for (int i=1; i<=n; i++)
	{
		if (out[i])
			continue;
		vi cyk;
		int v=i;
		while(cyk.empty() || v!=i)
		{
			cyk.push_back(v);
			out[v]=1;
			v=tab[v];
		}
		debug() << cyk;
		int r=cyk.size();
		for (int i=0; i<r; i++)
		{
			if (blok[cyk[i]])
			{
				vi pom;
				for (int j=0; j<r; j++)
					pom.push_back(cyk[(i+j)%r]);
				cyk=pom;
				break;
			}
		}
		if (blok[cyk[0]])
		{
			int v=0;
			while(v<r)
			{
				if (blok[cyk[v]])
				{
					v++;
					continue;
				}
				int u=v;
				while(u<r && !blok[cyk[u]])
					u++;
				vi ter;
				for (int i=v; i<u; i++)
					ter.push_back(cyk[i]);
				for (int j=0; j<(int)ter.size(); j+=2)
					biore[ter[j]]=1;
				v=u;
			}
		}
		else
		{
			if (r&1)
				nope();
			for (int i=0; i<r; i+=2)
				biore[cyk[i]]=1;
		}
	}
	vi res;
	for (int i=1; i<=n; i++)
		if (biore[i])
			res.push_back(tab[i]);
	printf("%d\n", (int)res.size());
	for (int i : res)
		printf("%d ", i);
	printf("\n");
	return 0;
}
