using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define foreach(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

int A[20][20],O[20][20],X[20][20];
char buf[1000010];
string s;

int sz;
int st[1000010];
int toR[1000010],toL[1000010];

int func(int pos){
    if(s[pos] != '('){
        if(s[pos] == '0') return 1;
        if(s[pos] == '1') return 8;
        return 6;
    }

    int L = pos + 1;
    int R = ((s[L] == '(') ? toR[L]+1 : L+1) + 1;
    int x = func(L), y = func(R);
    char oper = s[R-1];
//  cout << pos << ' ' << x << ' ' << oper << ' ' << y << endl;
    if(oper == '&') return A[x][y];
    if(oper == '|') return O[x][y];
    return X[x][y];
}

int parse(void){
    int N=s.length(),i,j;

    REP(i,N){
        if(s[i] == '('){
            st[sz] = i;
            sz++;
        } else if(s[i] == ')'){
            j = st[sz-1];
            sz--;
            toR[j] = i;
            toL[i] = j;
        }
    }

    return func(0);
}

int main(void){
    int i,j,x1,x2,y1,y2,z1,z2;

    REP(i,16) REP(j,16) REP(x1,2) REP(x2,2) REP(y1,2) REP(y2,2){
        if(x1 == 0 && x2 == 0 && !(i & 1)) continue;
        if(x1 == 0 && x2 == 1 && !(i & 2)) continue;
        if(x1 == 1 && x2 == 0 && !(i & 4)) continue;
        if(x1 == 1 && x2 == 1 && !(i & 8)) continue;

        if(y1 == 0 && y2 == 0 && !(j & 1)) continue;
        if(y1 == 0 && y2 == 1 && !(j & 2)) continue;
        if(y1 == 1 && y2 == 0 && !(j & 4)) continue;
        if(y1 == 1 && y2 == 1 && !(j & 8)) continue;

        {
            z1 = (x1 & y1);
            z2 = (x2 & y2);
            if(z1 == 0 && z2 == 0) A[i][j] |= 1;
            if(z1 == 0 && z2 == 1) A[i][j] |= 2;
            if(z1 == 1 && z2 == 0) A[i][j] |= 4;
            if(z1 == 1 && z2 == 1) A[i][j] |= 8;
        }

        {
            z1 = (x1 | y1);
            z2 = (x2 | y2);
            if(z1 == 0 && z2 == 0) O[i][j] |= 1;
            if(z1 == 0 && z2 == 1) O[i][j] |= 2;
            if(z1 == 1 && z2 == 0) O[i][j] |= 4;
            if(z1 == 1 && z2 == 1) O[i][j] |= 8;
        }

        {
            z1 = (x1 ^ y1);
            z2 = (x2 ^ y2);
            if(z1 == 0 && z2 == 0) X[i][j] |= 1;
            if(z1 == 0 && z2 == 1) X[i][j] |= 2;
            if(z1 == 1 && z2 == 0) X[i][j] |= 4;
            if(z1 == 1 && z2 == 1) X[i][j] |= 8;
        }
    }

    int N;

    scanf("%d",&N);
    scanf("%s",buf);
    s = buf;

    int ans = parse();

    if(ans&6){
        printf("YES\n");
    } else {
        printf("NO\n");
    }

    return 0;
}
