#include <cassert>
using namespace std;

const int maxn = 300;
const int inf = 1000000000;

int n;
int a[maxn][maxn];

int opt[2][maxn][maxn];

int main()
{
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%d", &a[i][j]);
        }
    }
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                opt[i][j][k] = -inf;
            }
        }
    }
    opt[1][0][0] = 0;
    for (int row = 0; row < n; row ++) {
        int *b = a[row];
        for (int i = 0; i < n; i++) {
            for (int j = i; j < n; j++) {
                opt[0][i][j] = opt[1][i][j];
                if (opt[0][i][j] != -inf) {
                    opt[0][i][j] += b[i];
                }
                opt[1][i][j] = -inf;
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = i; j < n; j++) {
                if (opt[0][i][j] == -inf) {
                    continue;
                }
                opt[1][i][j] = max(opt[1][i][j], opt[0][i][j] + (i < j ? b[j] : 0));
                if (i + 1 < n) {
                    int ni = i + 1, nj = max(i + 1, j);
                    opt[0][ni][nj] = max(opt[0][ni][nj], opt[0][i][j] + b[ni]);
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = i; j < n; j++) {
                if (opt[1][i][j] == -inf) {
                    continue;
                }
                if (j + 1 < n) {
                    opt[1][i][j + 1] = max(opt[1][i][j + 1], opt[1][i][j] + b[j + 1]);
                }
            }
        }
    }
    cout << opt[1][n - 1][n - 1] << endl;
}
