using pair2 = pair<T, T>;
using pii = pair<int, int>;
using pli = pair<ll, int>;
using pll = pair<ll, ll>;
mt19937_64 rng(chrono::steady_clock::now().time_since_epoch().count());
ll myRand(ll B) {
	return (ull)rng() % B;
}

#define pb push_back
#define mp make_pair
#define all(x) (x).begin(),(x).end()
#define fi first
#define se second

clock_t startTime;
double getCurrentTime() {
	return (double)(clock() - startTime) / CLOCKS_PER_SEC;
}

const int N = 300300;
ll a[N];
int k;
map<ll, pii> mem;
map<ll, int> firstOcc;

void solve() {
	scanf("%d", &k);
	int n = 1 << (k + 1);
	a[0] = 0;
	for (int i = 0; i < n; i++) {
		scanf("%lld", &a[i + 1]);
		a[i + 1] ^= a[i];
	}
	eprintf("read\n");
	firstOcc.clear();
	int prevZero = -1;
	for (int i = 0; i <= n; i++) {
		if (firstOcc.count(a[i])) {
			int p = firstOcc[a[i]];
			if (p != i - 1) {
				printf("%d %d %d %d\n", p + 1, p + 1, p + 2, i);
				return;
			}
			if (prevZero != -1) {
				printf("%d %d %d %d\n", prevZero, prevZero, i, i);
				return;
			}
			prevZero = i;
		} else {
			firstOcc[a[i]] = i;
		}
	}
	eprintf("before while true\n");
	mem.clear();
	while(true) {
		int l = myRand(n + 1), r = myRand(n + 1);
		if (l == r) continue;
		if (l > r) swap(l, r);
		ll x = a[l] ^ a[r];
		if (mem.count(x)) {
			pii t = mem[x];
			if (t.first == l || t.second == r) continue;
			vector<int> w = {t.first, t.second, l, r};
			sort(all(w));
			assert(w[0] < w[1] && w[1] <= w[2] && w[2] < w[3]);
			printf("%d %d %d %d\n", w[0] + 1, w[1], w[2] + 1, w[3]);
			return;
		} else {
			mem[x] = mp(l, r);
		}
	}
}

int main()
{
