using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define snuke(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

#define M 1000000

int K;
int s[M+10];

bool check(int p){
    for(int x=0;x<=M;x+=p){
        int ymax = min(x+p-1, M);
        int ymin = ((x == 0) ? 1 : (x+K+1));
        if(ymin <= ymax && s[ymin] < s[ymax+1]) return false;
    }
    return true;
}

int main(void){
    int N,x,i;

    cin >> N >> K;
    REP(i,N){
        scanf("%d", &x);
        s[x+1] = 1;
    }

    REP(i,M+5) s[i+1] += s[i];

    for(i=M;i>=2;i--) if(check(i)) break;
    cout << i << endl;

    return 0;
}
