#pragma comment(linker, "/STACK:1000000000")
#define _CRT_SECURE_NO_WARNINGS
#include <iostream>
#include <cstdio>
#include <vector>
#include <algorithm>
#include <string>
#include <cstring>
#include <queue>
#include <deque>
#include <map>
#include <unordered_map>
#include <set>
#include <unordered_set>
#include <bitset>
#include <memory>
#include <cassert>
#include <cstdlib>
#include <cmath>
#include <ctime> 
#include <stack>

#define mp make_pair
#define pb push_back

typedef long long ll;
typedef long double ld;
typedef unsigned int uint;

using namespace std;

const int MAXN = 100001;
const int INF = 1000 * 1000 * 1000 + 2;
const int MODULE = 1000 * 1000 * 1000 + 7;

struct lane
{
	int l, r, s, u;
};

stack <pair <int, int> > sc[MAXN];
int top[MAXN];
lane a[MAXN];
int rid[MAXN];



void er(int id)
{
	sc[id].pop();
	top[id] = sc[id].top().first;
}

bool cmp(lane l1, lane l2)
{
	return l1.s > l2.s;
}

int rmq[4 * MAXN];

inline int bst(int i1, int i2)
{
	return (top[i1] < top[i2]) ? i1 : i2;
}

void create_rmq(int v, int l, int r)
{
	if (l == r)
	{
		rid[l] = v;
		rmq[v] = l;
		return;
	}
	create_rmq(v * 2 + 1, l, (l + r) / 2);
	create_rmq(v * 2 + 2, (l + r) / 2 + 1, r);
	rmq[v] = bst(rmq[v * 2 + 1], rmq[v * 2 + 2]);
}

int lv, rv;

int get_rmq(int v, int l, int r)
{
	if ((lv > r) || (l > rv)) return 0;
	if ((lv <= l) && (r <= rv)) return rmq[v];
	return bst(get_rmq(v * 2 + 1, l, (l + r) / 2), get_rmq(v * 2 + 2, (l + r) / 2 + 1, r));
}

void modify_rmq(int l)
{
	int v = rid[l];
	while (v != 0)
	{
		v = (v - 1) / 2;
		rmq[v] = bst(rmq[v * 2 + 1], rmq[v * 2 + 2]);
	}
}

void psh(int id, int x, int y)
{
	sc[id].push(mp(x, y));
	top[id] = x;
	modify_rmq(id);
}

int main()
{
	ios_base::sync_with_stdio(0);
	//freopen("input.txt", "r", stdin); freopen("output.txt", "w", stdout);
	int h, w, n;
	cin >> h >> w >> n;
	top[0] = 2 * INF + 4;
	for (int i = 1; i <= w; i++)
	{
		psh(i, 2 * INF + 3, 0);
		psh(i, h + 1, 1);
	}
	create_rmq(0, 1, w);
	for (int i = 0; i < n; i++)
	{
		cin >> a[i].s >> a[i].l >> a[i].r >> a[i].u;
	}
	sort(a, a + n, cmp);
	for (int i = 0; i < n; i++)
	{
		lv = a[i].l, rv = a[i].r;
		int y = a[i].s;
		int ly = a[i].s + a[i].u;
		int cur = 0;
		while (true)
		{
			int v = get_rmq(0, 1, w);
			if (top[v] > ly) break;
			cur = (cur + sc[v].top().second) % MODULE;
			er(v);
			modify_rmq(v);
		}
		if (lv == 1)
			psh(rv + 1, y, (cur * 2) % MODULE);
		else if (rv == w) psh(lv - 1, y, (cur * 2) % MODULE);
		else psh(lv - 1, y, cur), psh(rv + 1, y, cur);
	}
	int ans = 0;
	for (int i = 1; i <= w; i++)
	{
		while (!sc[i].empty())
		{
			ans += sc[i].top().second;
			ans %= MODULE;
			sc[i].pop();
		}
	}
	cout << ans << endl;
	return 0;
}
