using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define snuke(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

int p[2010];

int main(void){
    int N,ans=0,i,j;

    cin >> N;
    REP(i,N) cin >> p[i];

    REP(i,N) if(p[i] < 0) p[i] = -p[i];

    REP(i,N){
        int a = 0, b = 0;
        REP(j,N) if(p[j] < p[i]){
            if(j < i) a++;
            if(j > i) b++;
        }
        ans += min(a, b);
    }

    cout << ans << endl;

    return 0;
}
