using namespace std;

#define REP(i,n) for((i)=0;(i)<(int)(n);(i)++)
#define foreach(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

typedef long long ll;

int K,a[110];
ll dp[102][300010];

ll func(int pos, ll N){
    if(pos == 0) return N;
    if(N < 300000 && dp[pos][N] != -1) return dp[pos][N];
    ll ans = func(pos-1,N) - func(pos-1,N/a[pos-1]);
    if(N < 300000) dp[pos][N] = ans;
    return ans;
}

int main(void){
    ll N;
    int i,j;

    cin >> N;
    cin >> K;
    REP(i,K) cin >> a[i];
    sort(a,a+K);
    REP(i,K+1) REP(j,300010) dp[i][j] = -1;

    ll ans = func(K,N);
    cout << ans << endl;

    return 0;
}
