{
	scanf("%d%d",&n,&m);
	for(i=1;i<=n;++i)scanf("%lld",a+i);
	for(i=1;i<=m;++i)scanf("%lld",b+i);
	for(k=1;k<=n;++k)
	{
		swap(a[k],a[n]);
		*a=-1e18;
		for(i=1;i<n;++i)
			for(j=1;j<=m;++j)
				*a=max(a[i]*b[j],*a);
		t=min(t,*a);
		swap(a[k],a[n]);
	}
	printf("%lld",t);
}
