#include <bits/stdc++.h>
using namespace std;

const int N = 110;
char a[N];

int main() {
#ifndef ONLINE_JUDGE
	freopen("in.txt", "r", stdin);
	freopen("out.txt", "w", stdout);
#endif
	ios_base::sync_with_stdio(0);
	cin.tie(0); cout.tie(0);
	int n; cin >> n;
	cin >> a;
	int now = 0;
	int Min = 0;
	for (int i = 0; i < n; i++) {
		if (a[i] == '-') now--;
		else now++;
		Min = min(Min, now);
	}
	if (Min < 0) now = -Min;
	else now = 0;
	for (int i = 0; i < n; i++) {
		if (a[i] == '-') now--;
		else now++;
	}
	cout << now << endl;
	return 0;
}
