#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);
	int t;
	cin >> t;
	for (; t--;) {
		int n;
		cin >> n;
		vector<int>a(n);
		for (int i = 0; i ^ n; ++i)
			cin >> a[i];
		vector<int>c1(11);
		for (int i = 0; i ^ n; ++i) {
			for (int j = 0; j ^ 11; ++j)
				if (a[i] >> j & 1)
					++c1[j];
		}
		int ans = 0;
		for (int i = 0; i ^ 11; ++i)
			if (c1[i] < n && c1[i] > 0)
				ans |= 1 << i;
		cout << ans << '\n';
	}
}
