#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>

using namespace std;

int main()
{
  int A,B,N;
  scanf("%d%d%d",&A,&B,&N);
  int Temp=A;
  static int Ans[100000];
  for(int i=0;i<N;i++)
    {
      Ans[i]=-1;
      for(int j=0;j<10;j++)
        if(!((A*10+j)%B))
          Ans[i]=j;
      if(Ans[i]==-1)
        {
          printf("-1\n");
          return 0;
        }
      A=0;
    }
  printf("%d",Temp);
  for(int i=0;i<N;i++)
    putchar(Ans[i]+48);
  putchar('\n');
  return 0;
}
