#include<bits/stdc++.h>
using namespace std;
const long long M=75161927681ll;
const int N=1505,E=524288;
int n,m,i,j,a[N],b[N],tu[N],t;
int main()
{
#ifndef ONLINE_JUDGE
	freopen("a.in","r",stdin);
	freopen("a.out","w",stdout);
#endif
	scanf("%d",&t);
	while(t--)
	{
		scanf("%d",&n);
		for(i=1;i<=n;++i)
			scanf("%d %d",&a[i],&b[i]);
		for(i=1;i<=n;++i)
			scanf("%d",&tu[i]);
		int s=0;
		for(i=1;i<=n;++i)
		{
			s+=a[i]-b[i-1]+tu[i];
			if(i!=n)
				s=max(b[i],s+(b[i]-a[i]+1)/2);
		}
		printf("%d\n",s);
	}
}
