#include <cstdio>
#include <vector>
using namespace std;

int use[110], a[110];

int main() {

    int n, K;
    scanf("%d%d", &n, &K);
    for (int i = 0; i < n; i ++) {
        scanf("%d", &a[i]);
    }
    vector<int> sol;
    while (true) {
        int id = -1;
        for (int i = 0; i < n; i ++) {
            if (!use[i] && (id == -1 || a[i] < a[id])) {
                id = i;
            }
        }
        if (id != -1 && a[id] <= K) {
            K -= a[id];
        } else {
            id = -1;
        }
        if (id == -1) {
            break;
        }
        use[id] = 1;
        sol.push_back(id + 1);
    }
    printf("%d\n", sol.size());
    for (auto x : sol) {
        printf("%d ", x);
    }
    puts("");
    return 0;
}
