#include <bits/stdc++.h>
using namespace std;

int work(string s) {
  int step = 0;
  while (1) {
    bool changed = false;

    for (int i = (int)s.length()-2 ; i >= 0 ; i--) {
      // cout << s[i] << s[i+1] << endl;
      if (s[i] == 'A' && s[i+1] == 'P') {
        changed = true;
        s[i+1] = 'A';
      }
    }

    if (!changed) {
      return step;
    }

    step++;
  }
}

int main() {
  int t; cin >> t;

  for (int tc = 1 ; tc <= t ; tc++) {
    int n; cin >> n;
    string s; cin >> s;

    cout << work(s) << endl;
  }
  return 0;
}
