//program 300-A

#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>

using namespace std;

int Get()
{
  char c;
  while(c=getchar(),c<'0'||c>'9');
  int X=0;
  while(c>='0'&&c<='9')
    {
      X=X*10+c-48;
      c=getchar();
    }
  return X;
}

int main()
{
  int N=Get();
  static int A[100000];
  for(int i=0;i<N;i++)
    scanf("%d",&A[i]);
  static bool Flag[100000];
  memset(Flag,0,sizeof(Flag));
  int P=0;
  while(A[P]>=0)
    P++;
  Flag[P]=true;
  printf("1 %d\n",A[P]);
  int Q=0;
  while(Q<N&&A[Q]<=0)
    Q++;
  if(Q==N)
    {
      int Q0=P+1;
      while(A[Q0]>=0)
        Q0++;
      int Q1=Q0+1;
      while(A[Q1]>=0)
        Q1++;
      Flag[Q0]=Flag[Q1]=true;
      printf("2 %d %d\n",A[Q0],A[Q1]);
    }
  else
    {
      printf("1 %d\n",A[Q]);
      Flag[Q]=true;
    }
  int Count=0;
  for(int i=0;i<N;i++)
    if(!Flag[i])
      Count++;
  printf("%d",Count);
  for(int i=0;i<N;i++)
    if(!Flag[i])
      printf(" %d",A[i]);
  putchar('\n');
  return 0;
}
