using namespace std;
int A[100000];
int B[100000];
int na;
int nb;
int main()
{
    cin >> na >> nb;
    int k, m;
    cin >> k >> m;
    for (int i=0; i<na; i++)
        cin >> A[i];
    for (int b=0; b<nb; b++)
        cin >> B[b];
    if (A[k-1]<B[nb-m])
        cout << "YES" << endl;
    else
        cout << "NO" << endl;
}
