#include <utility>
#include <time.h>
#include <string.h>
using namespace std;


int main( void )
{
	int n;
	cin >> n;
	vector<int> a(n);
	for( int i = 0; i < n; i ++ )
		cin >> a[i];

	int M = 0, Mi = -1;
	for( int i = 0; i < n; i ++ )
		if( M < a[i] )
			M = a[i], Mi = i;
	int m = 1000, mi = -1;
	for( int i = n - 1; i >= 0; i -- )
		if( m > a[i] )
			m = a[i], mi = i;
	if( Mi < mi ){
		cout << (Mi + (n-1-mi)) << endl;
	}
	else{
		cout << (Mi + (n-1-mi) - 1) << endl;
	}
}
