#include <bits/stdc++.h>

using namespace std;

int main() {
    int t; cin>>t;
    for (int ti = 0; ti < t; ++ti) {
        int n; cin>>n;
        vector<int> v(n);
        for (auto& a : v) {
            cin>>a;
        }
        int ans = 0;
        for (auto a : v) {
            if (a < 0) {
                cout << a << "\n";
                goto next;
            }
            ans = max(ans,a);
        }
        cout << ans << "\n";
    next:;}
}
