#include<stdio.h>
#include<string.h>

char c[100005];
int n,m;

int main()
{
	int i,j,a;
	n=0;
	m=0;
	scanf("%s",c);
	n=strlen(c);
	for(i=0;i<n;i++)
	{
		a=0;
		for(j=0;j<i;j++)
		{
			if(c[i]==c[j])
				a++;
		}
		if(a==0)
			m++;
	}
	if(m%2==0)
		printf("CHAT WITH HER!");
	else
		printf("IGNORE HIM!");
	return 0;
}
