#include <stdio.h>
#include <string.h>
#include <algorithm>

using namespace std;

typedef long long ll;
typedef unsigned long long ull;

int main(void)
{
    freopen("input.txt","rt",stdin);
    freopen("output.txt","wt",stdout);
    int n = 0;
    int m = 0;
    scanf("%d %d",&n,&m);
    int all = n+m;
    int l = 0;
    if(m > n) l = 1;
    for(int i = 0;i < all;i++)
    {
        if(l && m) { putchar('G'); m--;}
        else if(n) { putchar('B');n--; }
        else { putchar('G'); m--; }
        l ^= 1;
    }
    puts("");
    while(getchar() != EOF);
    return 0;
}
