#include <cstdio>
#include <cmath>
#include <algorithm>
#include <string.h>
using namespace std;

int a[52], n;
void srt()
{
	for (int i = 0; i+1< n; i++)
		for (int j = i; j+1<n; j++)
			if (a[j]>a[j+1]) swap(a[j], a[j+1]);
}
int main()
{
	scanf("%d", &n);
	for (int i=0; i<n; i++) a[i]=n-i;
	srt();
	bool ok=1;
	for (int i=0; i + 1 < n; i++)
		if (a[i]>a[i+1]) ok=0;
	if (ok) puts("-1");
	else for (int i = 0 ; i <n; i++) printf("%d ", n-i);
	return 0;
}
