#include <iostream>
#include <cstdio>
#include <cmath>
#include <cstring>
#include <vector>
#include <algorithm>
#include <cstdlib>
#include <string>
#include <queue>
#include <map>
#include <set>

#define MAX 1024

using namespace std;
FILE *in; FILE *out;

int main(void)
{
	in = stdin; out = stdout;
//	in = fopen("A.in", "rt"); out = fopen("A.out", "wt");

	int k;
	int a[12];
	fscanf(in, "%d", &k);
	for (int i = 0; i < 12; i++)
		fscanf(in, "%d", &a[i]);

	int ans = 13;
	for (int mask = 0; mask < (1 << 12); mask++)
	{
		int cnt = 0, sum = 0;
		for (int i = 0; i < 12; i++) if (mask & (1 << i))
		{
			cnt++;
			sum += a[i];
		}
		if (sum >= k) ans = min(ans, cnt);
	}
	fprintf(out, "%d\n", ans > 12 ? -1 : ans);

	return 0;
}
