#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
struct node
{
	int i,x;
}a[8000005];
bool cmp( node c, node d )
{
	if ( c.x != d.x )
		return c.x < d.x;
	else return c.i < d.i;
}
int main()
{
	int n;
	freopen("input.txt","r",stdin);
	freopen("output.txt","w",stdout);
	scanf( "%d", &n );
	for ( int i = 0; i < 2 * n; i++ )
	{
		scanf( "%d", &a[i].x );
		a[i].i = i;
	}
	sort( a, a + 2 * n, cmp );
	bool u = true;
	for ( int i = 0; i < 2 * n; i ++ )
	if ( i % 2 == 0 )
	{
		if ( a[i].x != a[i+1].x )
		{
			u = false;
			puts("-1");
			break;
		}
	}
	if ( u )
	{
		for ( int i = 0; i < 2 * n; i++ )
		if ( i % 2 == 0 )
			printf( "%d %d\n", a[i].i+1, a[i+1].i+1 );

	}
	return 0;
}
