#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>
#define MAXN 100005
using namespace std ;
typedef long long LL ;

int N, M, A, B ;
int ans ;
int main()
{
	int i, j ;
	scanf("%d %d %d %d", &N, &M, &A, &B) ;
	ans = N*A ;
	for(i = 0;; i ++)
	{
		ans = min(ans, max(0, N-i*M)*A+i*B) ;
		if(i*M > N) break ;
	}
	printf("%d\n", ans) ;
	//system("pause") ;
	return 0 ;
}
