#include<algorithm>
#include<cstdio>
#include<vector>
int t;
int n,k;
int cou[26];
signed main(){
	scanf("%d",&t);
	for(int wc=1;wc<=t;wc++){
		for(int i=0;i<=25;i++)cou[i]=0;
		scanf("%d%d",&n,&k);
		for(int i=1;i<=n;i++){
			char ch=getchar();
			while(ch>'z'||ch<'a')ch=getchar();
			cou[ch-'a']++;
		}
		int ic=0;
		for(int i=0;i<=25;i++)ic+=(cou[i]&1);
		printf("%s\n",ic<=k+1?"YES":"NO");
	}
	return 0;
}
