using namespace std;

int a[10000], s[10000];

int main()
{
    int n, m;
    scanf("%d", & n);
    for (int i = 1; i <= n; ++ i)
    {
        scanf("%d", a + i);
        a[i + n] = a[i];
        a[i + n + n] = a[i];
    }
    s[1] = 0;
    for (int i = 2; i <= n + n + n; ++ i)
        s[i] = s[i - 1] + a[i - 1];
    int x, y;
    scanf("%d %d", & x, & y);
    if (x > y)
        swap(x, y);
    int s1 = s[x], s2 = s[y], s3 = s[x + n];
    printf("%d\n", min(s2 - s1, s3 - s2));
}
