#include <iostream>
#include <set>
#include <map>
#include <vector>


using namespace std;

int n, m;
int a[100];
bool can[100][100];


int main(){
    cin >> n >> m;
    for(int i=0; i<n; i++){
        cin >> a[i];
    }
    for(int i=0; i<m; i++){
        int x, y;
        cin >> x >> y;
        x--; y--;
        can[x][y] = can[y][x] = true;
    }

    int ans = -1;
    for(int i=0; i<n; i++){
        for(int j=0; j<n; j++)if(i!=j && can[i][j]){
            for(int k=0; k<n; k++)if(k!=i && k!=j && can[i][k] && can[j][k]){
                int s = a[i] + a[j] + a[k];
                if(ans==-1 || s<ans) 
                    ans = s;
            }
        }
    }
    cout << ans;

    return 0;
}
