#include <iostream>
#include <cstdio>
#include <cstring>
#include <cmath>
#include <string>
#include <vector>

using namespace std;

typedef long long LL;

const int maxN = 10;

int cnt[maxN];
int K;

int main()
{
	scanf("%d", &K);
	for (int i = 0; i < 4; ++i) {
		char s[5];
		scanf("%s", s);
		for (int j = 0; j < 4; ++j)
			if (s[j] == '.') ++cnt[0];
			else ++cnt[s[j] - '0'];
	}
	for (int i = 1; i < 10; ++i)
		if (cnt[i] > 2 * K) {
			printf("NO\n");
			return 0;
		}
	printf("YES\n");
	return 0;
}
