#include <cstdio>
const   int maxn = 2100;
int p, ans, twp, flag;
int main()
{
    scanf("%d", &p);
    if (p == 2) {printf("1\n"); return 0;}
    ans = 0;
    for (int i=1; i<p; i++)
    {
        twp = i;
        flag = 1;
        for (int j=1; j<p-1; j++)
        {
            if (!((twp + p - 1) % p)) {flag = 0; break;}
            twp = (twp * i) % p;
        }
        if (flag && (!((twp + p - 1) % p))) ans++;
    }
    printf("%d\n", ans);
    return 0;
}
