#include <cstdio>
#include <cstdlib>
using namespace std;
bool ok[7];
int n,x,p;
int main()
{
	scanf("%d%d",&n,&x),ok[x]=1,p=x,ok[7-x]=1;
	for(int i=1;i<=n;++i)
	{
		int a,b; scanf("%d%d",&a,&b);
		if(a==x||b==x||a==7-x||b==7-x)
		{
			printf("NO\n");
			return 0;
		}
	}
	printf("YES\n");
//	system("pause");
	return 0;
}
