#include <cstdio>
#include <algorithm>
using namespace std;

int main ()
{
	int n, a, b, c, d;
	scanf("%d", &n);
	for (int i = 0; i < 4; ++i)
	{
		scanf("%d%d%d%d", &a, &b, &c, &d);
		a = min(a, b);
		c = min(c, d);
		if (a + c <= n)
		{
			printf("%d %d %d\n", i + 1, a, n - a);
			return 0;
		}
	}
	printf("-1\n");
	return 0;
}
