#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

ll i,j,k,n,m,t;

int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);cout.tie(0);
	cin>>t;
	while(t--){
		cin>>n>>m;
		n--;m--;
		if(n>m)swap(n,m);
		if(n+m<=1){
			cout<<n+m<<'\n';continue;
		}
		if(!n||!m){
			cout<<"-1\n";continue;
		}
		cout<<m+m-((m-n)&1)<<'\n';
	}
}
