#include <stdio.h>
int u[1010], a[50];
int main(){
    int n, k, i, x;
    scanf("%d%d", &n, &k);
    for (i = 0; i < k; i++){
        scanf("%d", &x);
        a[i] = x;
        u[x] = 1;
    }
    for (i = 0; i < k; i++){
        printf("%d", a[i]);
        int t = n - 1;
        x = 1;
        while (t){
            if (u[x]==0){
                u[x]=1;
                printf(" %d", x);
                t--;
            }
            x++;
        }
        printf("\n");
    }
    return 0;
}
