#include <stdio.h>
int main(){
    int x, y, n;
    scanf("%d%d%d", &x, &y, &n);
    if (x < 0) x = -x;
    if (y < 0) y = -y;
    if (x + y > n) {
        printf("No\n");
    } else if ((x + y - n) % 2) {
        printf("No\n");
    } else {
        printf("Yes\n");
    }
    return 0;
}
