 #include <iostream>
#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;
typedef long long ll;
typedef pair<int, int> mypair;
inline int read()
{
	int x = 0, f = 1;
	char c = getchar();
	while (!isdigit(c))
	{
		if (c == '-') f = -1;
		c = getchar();
	}
	while (isdigit(c))
	{
		x = x * 10 + c - '0';
		c = getchar();
	}
	return x * f;
}

int n, a[4000005];

int main()
{
	n = read(); n <<= 1;
	for (int i = 1; i <= n; i ++) a[i] = read();
	sort(a + 1, a + 1 + n);
	ll ans = 0, s = 0;
	for (int i = 1; i <= n; i ++)
	{
		if (i <= n / 2) s += a[i];
		ans += a[i];
	}
	if (s * 2ll == ans) puts("-1");
	else for (int i = 1; i <= n; i ++) printf("%d%c", a[i], i == n ? 10 : 32);
	return 0;
}
