#include <cstdio>
#include <cstring>

int a[105][105];
int win_cnt[105];

int main() {
    int n,m;
    scanf("%d%d",&m,&n);
    int i,j;
    for (i=1;i<=n;i++) {
        for (j=1;j<=m;j++) {
            scanf("%d",a[i]+j);
        }
    }
    for (i=1;i<=n;i++) {
        int x=1;
        for (j=2;j<=m;j++) {
            if (a[i][j]>a[i][x]) x=j;
        }
        ++win_cnt[x];
    }
    int ans=1;
    for (i=2;i<=m;i++) {
        if (win_cnt[i]>win_cnt[ans]) ans=i;
    }
    printf("%d\n",ans);
}
